import { z } from 'zod';
import { 
  validateCollectionData, 
  getSchemaForCollection, 
  type DocumentValidationResult 
} from '@/lib/dataValidationUtils';
import * as logger from '@/lib/logger';
import * as localDb from '@/lib/localDb';

// Mock the logger and localDb modules
jest.mock('@/lib/logger', () => ({
  logInfo: jest.fn(),
  logWarn: jest.fn(),
  logError: jest.fn()
}));

jest.mock('@/lib/localDb', () => ({
  getUsers: jest.fn(),
  getPatients: jest.fn(),
  getDoctors: jest.fn(),
  getAppointments: jest.fn(),
  getNotifications: jest.fn()
}));

describe('dataValidationUtils', () => {
  // Reset mocks between tests
  beforeEach(() => {
    jest.clearAllMocks();
  });

  describe('getSchemaForCollection', () => {
    it('should return appropriate schema for each collection', () => {
      expect(getSchemaForCollection('users')).toBeDefined();
      expect(getSchemaForCollection('patients')).toBeDefined();
      expect(getSchemaForCollection('doctors')).toBeDefined();
      expect(getSchemaForCollection('appointments')).toBeDefined();
      expect(getSchemaForCollection('notifications')).toBeDefined();
    });

    it('should return undefined for unknown collections', () => {
      expect(getSchemaForCollection('unknown')).toBeUndefined();
    });
  });

  describe('validateCollectionData', () => {
    // Create a simple test schema for validation
    const TestSchema = z.object({
      name: z.string(),
      age: z.number().min(18)
    });

    it('should validate valid documents correctly', async () => {
      // Mock valid data return
      const mockData = [
        { id: '1', name: 'John', age: 30 },
        { id: '2', name: 'Jane', age: 25 }
      ];

      // Implement mock for getUsers
      (localDb.getUsers as jest.Mock).mockResolvedValue(mockData);

      const results = await validateCollectionData('users', TestSchema);

      // Expect 2 valid results
      expect(results).toHaveLength(2);
      expect(results[0].status).toBe('valid');
      expect(results[1].status).toBe('valid');

      // Verify logger was called correctly
      expect(logger.logInfo).toHaveBeenCalledWith('Starting validation for collection: users');
      expect(logger.logInfo).toHaveBeenCalledWith('Fetched 2 documents from users');
      expect(logger.logInfo).toHaveBeenCalledWith('Validation finished for collection: users');
    });

    it('should identify invalid documents', async () => {
      // Mock data with one invalid document (age below minimum)
      const mockData = [
        { id: '1', name: 'John', age: 30 },
        { id: '2', name: 'Minor', age: 16 }  // Invalid age
      ];

      (localDb.getUsers as jest.Mock).mockResolvedValue(mockData);

      const results = await validateCollectionData('users', TestSchema);

      // Expect mixed results
      expect(results).toHaveLength(2);
      expect(results[0].status).toBe('valid');
      expect(results[1].status).toBe('invalid');
      expect(results[1].errors).toBeDefined();
      expect(results[1].errors?.length).toBeGreaterThan(0);
      expect(results[1].errors?.[0].field).toBe('age');

      // Verify warning was logged for invalid document
      expect(logger.logWarn).toHaveBeenCalled();
    });

    it('should handle missing or incorrect data types', async () => {
      // Mock null return (no data found)
      (localDb.getUsers as jest.Mock).mockResolvedValue(null);

      const results = await validateCollectionData('users', TestSchema);

      // Expect error result
      expect(results).toHaveLength(1);
      expect(results[0].status).toBe('error');
      expect(results[0].fetchError).toBeDefined();
    });

    it('should handle exceptions during data fetching', async () => {
      // Mock exception during data fetch
      (localDb.getUsers as jest.Mock).mockRejectedValue(new Error('Database connection error'));

      const results = await validateCollectionData('users', TestSchema);

      // Expect error result
      expect(results).toHaveLength(1);
      expect(results[0].status).toBe('error');
      expect(results[0].fetchError).toBe('Database connection error');

      // Verify error was logged
      expect(logger.logError).toHaveBeenCalled();
    });
  });
});

'use client';

import React, { useState } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { LogIn } from 'lucide-react';
import Card from '@/components/ui/Card';
import Input from '@/components/ui/Input';
import Button from '@/components/ui/Button';
import Alert from '@/components/ui/Alert';
import { logInfo, logError } from '@/lib/logger';
import { useAuth } from '@/context/AuthContext';

/**
 * Login Page
 * Allows users to authenticate into the application
 *
 * @returns Login form component
 */
export default function LoginPage() {
  const router = useRouter();
  const { login } = useAuth();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    rememberMe: false,
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      // Capture the form values to ensure they don't change during async operations
      const { email, password } = formData;

      // Validate email and password
      if (!email || typeof email !== 'string') {
        throw new Error('Valid email is required');
      }

      if (!password || typeof password !== 'string') {
        throw new Error('Valid password is required');
      }

      // Log authentication attempt
      logInfo('auth-event', {
        action: 'login-attempt',
        email,
        timestamp: new Date().toISOString(),
      });

      // Enhanced debug logging
      console.log('[LoginPage.handleSubmit] Login attempt with:', { 
        email, 
        passwordProvided: !!password,
        passwordLength: password ? password.length : 0
      });

      // Use the AuthContext login function with email and password
      console.log('About to call login with:', { 
        email, 
        password: password ? '(password provided)' : 'undefined',
        passwordType: typeof password
      });

      try {
        // Pass 'ACTUAL_LOGIN_IN_PROGRESS' to indicate a real login attempt (not a mock login)
        // This ensures the window.__mockLogin function doesn't interfere
        console.log('Login function type:', typeof login);

        const success = await login(email, password, 'ACTUAL_LOGIN_IN_PROGRESS');

        console.log('Login result:', success);

        if (success) {
          setIsLoading(false);
          logInfo('Login successful', { email });
          // Router redirection will be handled by AuthContext based on user role
        } else {
          setIsLoading(false);
          setError('Invalid credentials. Please try again.');
          logError('Login failed', { email });
        }
      } catch (loginErr) {
        console.error('Login error details:', loginErr);
        setIsLoading(false);
        setError(loginErr instanceof Error ? loginErr.message : 'Login process error. Please try again.');
        logError('Login error in try-catch', loginErr);
      }
    } catch (err) {
      console.error('Outer error details:', err);
      setIsLoading(false);
      const errorMessage = err instanceof Error ? err.message : 'Login failed. Please try again.';
      setError(errorMessage);
      logError('Login error in outer try-catch', err);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-slate-50 dark:bg-slate-900">
      <Card className="w-full max-w-md p-6 space-y-6">
        <div className="text-center">
          <div className="flex items-center justify-center">
            <h1 className="text-2xl font-bold text-slate-900 dark:text-white flex items-center gap-2">
              <LogIn size={20} /> Sign In
            </h1>
          </div>
          <p className="text-slate-600 dark:text-slate-400 mt-2">Access your health account</p>
        </div>

        {error && <Alert variant="error">{error}</Alert>}

        <form className="space-y-4" onSubmit={handleSubmit}>
          <Input
            id="email"
            name="email"
            type="email"
            label="Email Address"
            required
            placeholder="Enter your email"
            value={formData.email}
            onChange={handleChange}
          />

          <Input
            id="password"
            name="password"
            type="password"
            label="Password"
            required
            placeholder="Enter your password"
            value={formData.password}
            onChange={handleChange}
          />

          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <input
                id="rememberMe"
                name="rememberMe"
                type="checkbox"
                className="h-4 w-4 text-primary border-slate-300 rounded"
                checked={formData.rememberMe}
                onChange={handleChange}
              />
              <label
                htmlFor="rememberMe"
                className="ml-2 block text-sm text-slate-600 dark:text-slate-400"
              >
                Remember me
              </label>
            </div>

            <Link href="/auth/forgot-password" className="text-sm text-primary hover:underline">
              Forgot password?
            </Link>
          </div>

          <Button type="submit" className="w-full" isLoading={isLoading}>
            Sign In
          </Button>
        </form>

        <div className="text-center mt-4">
          <p className="text-sm text-slate-600 dark:text-slate-400">
            Don&apos;t have an account?{' '}
            <Link href="/auth/register" className="text-primary hover:underline">
              Register
            </Link>
          </p>
        </div>

        <div className="text-center mt-4 text-sm text-slate-500">
          <p>Test Accounts (use with password "password"):</p>
          <ul className="mt-2 space-y-1">
            <li>Patient: test-patient@example.com</li>
            <li>Doctor: test-doctor@example.com</li>
            <li>Admin: admin@example.com</li>
          </ul>
        </div>
      </Card>
    </div>
  );
}

'use client';

import { useEffect } from 'react';
import Link from 'next/link';
import Card from '@/components/ui/Card';
import Button from '@/components/ui/Button';
import Spinner from '@/components/ui/Spinner';
import { Users, UserRound, Stethoscope, ShieldAlert, ArrowRight } from 'lucide-react';
import { useAllUsers, useAllDoctors } from '@/data/adminLoaders';
import { VerificationStatus, UserType } from '@/types/enums';
import { logInfo, logValidation } from '@/lib/logger';

// API response interfaces
interface UsersApiResponse {
  success: boolean;
  users: User[];
}

interface DoctorsApiResponse {
  success: boolean;
  doctors: Doctor[];
}

// Stat component for dashboard statistics
function Stat({ 
  title, 
  value, 
  Icon, 
  isLoading = false 
}: { 
  title: string; 
  value: number | string; 
  Icon: React.ComponentType<any>;
  isLoading?: boolean;
}) {
  return (
    <Card className="p-6">
      <div className="flex items-center space-x-3 mb-3">
        <div className="p-2 bg-primary-50 dark:bg-primary-900/30 rounded-full">
          <Icon className="h-5 w-5 text-primary-600 dark:text-primary-400" />
        </div>
        <h3 className="font-medium text-slate-600 dark:text-slate-300">{title}</h3>
      </div>
      {isLoading ? (
        <div className="flex items-center h-8">
          <Spinner className="h-4 w-4" />
        </div>
      ) : (
        <p className="text-2xl font-bold">{value}</p>
      )}
    </Card>
  );
}

// Card header with link component
function HeaderWithLink({ title, href }: { title: string; href: string }) {
  return (
    <div className="p-4 border-b border-slate-200 dark:border-slate-700 flex justify-between items-center">
      <h2 className="text-lg font-medium">{title}</h2>
      <Link href={href}>
        <Button variant="ghost" size="sm">
          View all
          <ArrowRight className="ml-1 h-4 w-4" />
        </Button>
      </Link>
    </div>
  );
}

// For TypeScript
interface User {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  userType: UserType;
}

interface Doctor extends User {
  specialty: string;
  verificationStatus: VerificationStatus;
}

export default function AdminDashboard() {
  const { data: usersData, isLoading: usersLoading } = useAllUsers() as { 
    data: UsersApiResponse | undefined, 
    isLoading: boolean, 
    error: unknown 
  };

  const { data: doctorsData, isLoading: doctorsLoading } = useAllDoctors() as { 
    data: DoctorsApiResponse | undefined, 
    isLoading: boolean, 
    error: unknown 
  };

  // Calculate stats
  const totalUsers = usersData?.success ? usersData.users.length : 0;
  const totalPatients = usersData?.success 
    ? usersData.users.filter((user: User) => user.userType === UserType.PATIENT).length
    : 0;
  const totalDoctors = doctorsData?.success ? doctorsData.doctors.length : 0;
  const pendingVerifications = doctorsData?.success 
    ? doctorsData.doctors.filter((doctor: Doctor) => doctor.verificationStatus === VerificationStatus.PENDING).length
    : 0;

  // Get recent users and pending doctors
  const recentUsers = usersData?.success 
    ? usersData.users.slice(0, 5)
    : [];

  const pendingDoctors = doctorsData?.success 
    ? doctorsData.doctors.filter((doctor: Doctor) => doctor.verificationStatus === VerificationStatus.PENDING).slice(0, 5)
    : [];

  useEffect(() => {
    logInfo('Admin dashboard rendered (with real data)');

    // Log validation
    try {
      logValidation('4.10', 'success', 'Admin dashboard connected to real data via local API.');
    } catch (e) {
      console.error('Could not log validation', e);
    }
  }, []);

  return (
    <div className="space-y-10">
      <h1 className="text-2xl font-semibold mb-6 dark:text-white">Admin Dashboard</h1>

      {/* Stats grid */}
      <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        <Stat title="Total Users" value={totalUsers} Icon={Users} isLoading={usersLoading} />
        <Stat title="Patients" value={totalPatients} Icon={UserRound} isLoading={usersLoading} />
        <Stat title="Doctors" value={totalDoctors} Icon={Stethoscope} isLoading={doctorsLoading} />
        <Stat title="Pending Verifications" value={pendingVerifications} Icon={ShieldAlert} isLoading={doctorsLoading} />
      </section>

      {/* Recent users */}
      <Card>
        <HeaderWithLink title="Recent Users" href="/admin/users" />
        {usersLoading ? (
          <div className="p-6 flex justify-center">
            <Spinner />
          </div>
        ) : recentUsers.length > 0 ? (
          <div className="divide-y divide-slate-200 dark:divide-slate-700">
            {recentUsers.map((user: User, idx: number) => (
              <div key={user.id || user.email || idx} className="p-4 flex justify-between items-center">
                <div>
                  <p className="font-medium">{user.firstName} {user.lastName}</p>
                  <p className="text-sm text-slate-500 dark:text-slate-400">{user.email}</p>
                </div>
                <div>
                  <span className="text-xs px-2 py-1 rounded-full bg-slate-100 dark:bg-slate-800">
                    {user.userType}
                  </span>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="p-6 text-center text-slate-500 dark:text-slate-400">
            No users found
          </div>
        )}
      </Card>

      {/* Pending verifications */}
      <Card>
        <HeaderWithLink title="Pending Doctor Verifications" href="/admin/doctors?status=PENDING" />
        {doctorsLoading ? (
          <div className="p-6 flex justify-center">
            <Spinner />
          </div>
        ) : pendingDoctors.length > 0 ? (
          <div className="divide-y divide-slate-200 dark:divide-slate-700">
            {pendingDoctors.map((doctor: Doctor, idx: number) => (
              <div key={doctor.id || doctor.email || idx} className="p-4 flex justify-between items-center">
                <div>
                  <p className="font-medium">Dr. {doctor.firstName} {doctor.lastName}</p>
                  <p className="text-sm text-slate-500 dark:text-slate-400">{doctor.specialty}</p>
                </div>
                <Link href={`/admin/doctor-verification/${doctor.id}`}>
                  <Button size="sm" variant="secondary">Verify</Button>
                </Link>
              </div>
            ))}
          </div>
        ) : (
          <div className="p-6 text-center text-slate-500 dark:text-slate-400">
            No pending verifications
          </div>
        )}
      </Card>
    </div>
  );
}

'use client';

import React, { useEffect, useState } from 'react';
import { useParams, useRouter } from 'next/navigation';
import Card from '@/components/ui/Card';
import Link from 'next/link';
import Button from '@/components/ui/Button';
import VerificationForm from '@/components/admin/VerificationForm';
import { FileText, ExternalLink, User } from 'lucide-react';
import { useUserDetail, useUpdateDoctorVerification } from '@/data/adminLoaders';
import Spinner from '@/components/ui/Spinner';
import Alert from '@/components/ui/Alert';
import { VerificationStatus } from '@/types/enums';
import { logInfo, logValidation } from '@/lib/logger';
import Image from 'next/image';

type Document = {
  title: string;
  url: string;
  type: string;
};

// Helper component for placeholder content
function PlaceholderLine({ text }: { text: string }) {
  return (
    <div className="p-6 text-center text-slate-500 dark:text-slate-400">
      {text}
    </div>
  );
}

const DoctorVerificationPage = () => {
  const params = useParams();
  const router = useRouter();
  const doctorId = params?.doctorId as string;
  const [success, setSuccess] = useState(false);

  // Fetch doctor details
  const { 
    data: doctorData, 
    isLoading: isLoadingDoctor, 
    error: doctorError 
  } = useUserDetail(doctorId);

  // Get verification mutation
  const verifyDoctorMutation = useUpdateDoctorVerification();

  const [documents, setDocuments] = useState<Document[]>([]);
  const [isLoadingDocuments, setIsLoadingDocuments] = useState(false);
  const [documentError, setDocumentError] = useState<string | null>(null);

  useEffect(() => {
    logInfo('DoctorVerificationPage mounted', { doctorId });

    // If doctor data is available, extract documents
    if (doctorData?.user?.doctorProfile) {
      const profile = doctorData.user.doctorProfile;
      const docs: Document[] = [];

      if (profile.licenseDocumentUrl) {
        docs.push({
          title: 'Medical License',
          url: profile.licenseDocumentUrl,
          type: 'license'
        });
      }

      if (profile.certificateUrl) {
        docs.push({
          title: 'Medical Certificate',
          url: profile.certificateUrl,
          type: 'certificate'
        });
      }

      setDocuments(docs);
    }
  }, [doctorId, doctorData]);

  // Handle verification submission
  const handleVerification = async ({ status, notes }: { status: string; notes: string }) => {
    try {
      await verifyDoctorMutation.mutateAsync({ 
        doctorId, 
        status: status as VerificationStatus, 
        notes 
      });
      setSuccess(true);
      logInfo('Doctor verification updated', { doctorId, status });
      logValidation('4.10', 'success', 'Doctor verification fully functional with real API');

      // Redirect after successful verification
      setTimeout(() => {
        router.push('/admin/doctors');
      }, 1500);
    } catch (error) {
      console.error('Verification error:', error);
      throw error;
    }
  };

  useEffect(() => {
    logInfo('doctor-verification rendered (with real data)', { doctorId });
  }, [doctorId]);

  const doctor = doctorData?.user;
  const doctorProfile = doctor?.doctorProfile;

  if (isLoadingDoctor) {
    return (
      <div className="p-6 flex justify-center items-center min-h-screen">
        <Spinner size="lg" />
        <span className="ml-3 text-gray-600">Loading doctor information...</span>
      </div>
    );
  }

  if (doctorError) {
    return (
      <div className="p-6">
        <Alert variant="error">
          Error loading doctor: {doctorError instanceof Error ? doctorError.message : 'Unknown error'}
        </Alert>
      </div>
    );
  }

  if (!doctor || !doctorProfile) {
    return (
      <div className="p-6">
        <Alert variant="warning">Doctor not found or doctor profile not available</Alert>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-semibold dark:text-white">Doctor Verification</h1>
        <Button
          variant="outline"
          size="sm"
          as={Link}
          href="/admin/doctors"
        >
          Back to Doctors
        </Button>
      </div>

      {/* Doctor Information */}
      <Card>
        <div className="p-4 border-b border-slate-200 dark:border-slate-700">
          <h2 className="text-lg font-semibold">Doctor Information</h2>
        </div>

        <div className="p-6">
          <div className="space-y-4">
            <div className="flex flex-col md:flex-row md:items-center gap-4">
              <div className="flex-shrink-0 w-24 h-24 bg-slate-200 dark:bg-slate-700 rounded-full flex items-center justify-center">
                {doctorProfile.profilePictureUrl ? (
                  <Image
                    src={doctorProfile.profilePictureUrl}
                    alt={`${doctor.firstName} ${doctor.lastName}`}
                    width={96}
                    height={96}
                    className="w-full h-full rounded-full object-cover"
                  />
                ) : (
                  <div className="w-20 h-20 bg-gray-200 rounded-full flex items-center justify-center">
                    <span className="text-gray-500 text-xl">
                      {doctor.firstName?.[0]}{doctor.lastName?.[0]}
                    </span>
                  </div>
                )}
              </div>

              <div className="flex-grow">
                <h3 className="text-xl font-semibold">Dr. {doctor.firstName} {doctor.lastName}</h3>
                <p className="text-slate-600 dark:text-slate-400">
                  {doctorProfile?.specialty || 'General Practice'}
                </p>
                <p className="text-slate-500 dark:text-slate-500">
                  License: {doctorProfile?.licenseNumber || 'Not provided'}
                </p>
                <p className="text-slate-500 dark:text-slate-500">
                  Experience: {doctorProfile?.yearsOfExperience || '0'} years
                </p>
              </div>

              <div className="md:text-right">
                <div className="inline-block px-3 py-1 rounded-full text-sm" 
                  style={{
                    backgroundColor: doctor.verificationStatus === 'VERIFIED' 
                      ? 'rgba(16, 185, 129, 0.1)' 
                      : doctor.verificationStatus === 'REJECTED'
                        ? 'rgba(239, 68, 68, 0.1)'
                        : 'rgba(245, 158, 11, 0.1)',
                    color: doctor.verificationStatus === 'VERIFIED'
                      ? 'rgb(16, 185, 129)'
                      : doctor.verificationStatus === 'REJECTED'
                        ? 'rgb(239, 68, 68)'
                        : 'rgb(245, 158, 11)'
                  }}
                >
                  {doctor.verificationStatus || 'PENDING'}
                </div>
              </div>
            </div>
          </div>
        </div>
      </Card>

      {/* Uploaded Documents */}
      <Card>
        <div className="p-4 border-b border-slate-200 dark:border-slate-700">
          <h2 className="text-lg font-semibold">Uploaded Documents</h2>
        </div>

        {isLoadingDocuments ? (
          <div className="flex justify-center items-center p-6">
            <Spinner />
          </div>
        ) : documentError ? (
          <Alert variant="error" className="m-4">{documentError}</Alert>
        ) : documents.length === 0 ? (
          <div className="p-6 text-center text-slate-500 dark:text-slate-400">
            No documents have been uploaded
          </div>
        ) : (
          <div className="p-6">
            <div className="space-y-4">
              {documents.map((doc, idx) => (
                <div key={idx} className="flex items-center justify-between p-3 border border-slate-200 dark:border-slate-700 rounded-md">
                  <div className="flex items-center space-x-3">
                    <FileText className="h-5 w-5 text-slate-400" />
                    <span className="font-medium">{doc.title}</span>
                  </div>
                  <Link 
                    href={doc.url}
                    target="_blank"
                    className="text-primary-600 dark:text-primary-400 flex items-center hover:underline"
                  >
                    <span>View</span>
                    <ExternalLink className="ml-1 h-4 w-4" />
                  </Link>
                </div>
              ))}
            </div>
          </div>
        )}
      </Card>

      {/* Verification Form */}
      {!isLoadingDoctor && doctor && (
        <VerificationForm
          doctorId={doctorId}
          currentStatus={(doctor.verificationStatus || 'PENDING') as 'PENDING' | 'VERIFIED' | 'REJECTED'}
          onSubmit={handleVerification}
        />
      )}

      {success && (
        <Alert variant="success" className="mt-4">
          Verification status updated successfully. Redirecting to doctors list...
        </Alert>
      )}
    </div>
  );
};

export default DoctorVerificationPage;

'use client';

import { useState, useEffect } from 'react';
import { useParams, useRouter } from 'next/navigation';
import Link from 'next/link';
import Card from '@/components/ui/Card';
import Button from '@/components/ui/Button';
import Textarea from '@/components/ui/Textarea';
import Alert from '@/components/ui/Alert';
import Spinner from '@/components/ui/Spinner';
import { 
  CalendarDays, 
  Clock, 
  ChevronLeft, 
  CheckCircle,
  MapPin,
  VideoIcon,
  Calendar
} from 'lucide-react';
import { logInfo, logValidation } from '@/lib/logger';
import { useAuth } from '@/context/AuthContext';
import { useDoctorProfile, useDoctorAvailability } from '@/data/sharedLoaders';
import { useBookAppointment } from '@/data/sharedLoaders';
import { format, addDays } from 'date-fns';
import { AppointmentType } from '@/types/enums';
import Image from 'next/image';
import { z } from 'zod';
import { DoctorProfile, TimeSlot } from '@/types/schemas';

// Define the merged doctor profile type based on API response
interface DoctorPublicProfile extends Omit<DoctorProfile, 'servicesOffered' | 'educationHistory' | 'experience'> {
  id: string;
  firstName: string;
  lastName: string;
  rating?: number;
  reviewCount?: number;
  servicesOffered?: string[];
  educationHistory?: { institution: string; degree: string; year: string }[];
  experience?: { position: string; hospital: string; duration: string }[];
}

export default function BookAppointmentPage() {
  const params = useParams();
  const router = useRouter();
  const { user } = useAuth();
  const doctorId = params?.doctorId as string;

  // Fetch doctor profile and availability
  const { data: doctorData, isLoading: doctorLoading, error: doctorError } = useDoctorProfile(doctorId) as {
    data?: { success: boolean; doctor: DoctorPublicProfile };
    isLoading: boolean;
    error: unknown;
  };
  const { data: availabilityData, isLoading: availabilityLoading, error: availabilityError } = useDoctorAvailability(doctorId) as {
    data?: { success: boolean; availability: { weeklySchedule: Record<string, TimeSlot[]>; blockedDates: string[] } };
    isLoading: boolean;
    error: unknown;
  };
  const bookAppointmentMutation = useBookAppointment();

  // State variables
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState<string | null>(null);
  const [selectedEndTime, setSelectedEndTime] = useState<string | null>(null);
  const [appointmentType, setAppointmentType] = useState<AppointmentType>(AppointmentType.IN_PERSON);
  const [reason, setReason] = useState('');
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Generate available dates (next 14 days)
  const generateAvailableDates = () => {
    const dates = [];
    const currentDate = new Date();

    for (let i = 1; i <= 14; i++) {
      const date = addDays(currentDate, i);
      dates.push(date);
    }

    return dates;
  };

  const availableDates = generateAvailableDates();

  // Get available time slots for selected date
  const getAvailableTimeSlots = () => {
    if (!selectedDate || !availabilityData?.success) return [];
    const { availability } = availabilityData;
    const dayOfWeek = format(selectedDate, 'EEEE').toLowerCase();
    const dateString = format(selectedDate, 'yyyy-MM-dd');
    // Check if date is blocked
    if (Array.isArray(availability.blockedDates) && availability.blockedDates.includes(dateString)) {
      return [];
    }
    // Get slots for the day
    if (
      availability.weeklySchedule &&
      Object.prototype.hasOwnProperty.call(availability.weeklySchedule, dayOfWeek) &&
      Array.isArray(availability.weeklySchedule[dayOfWeek])
    ) {
      return (availability.weeklySchedule[dayOfWeek] as TimeSlot[])
        .filter((slot) => slot.isAvailable)
        .map((slot) => ({
          startTime: slot.startTime,
          endTime: slot.endTime,
        }));
    }
    return [];
  };

  const availableTimeSlots = getAvailableTimeSlots();

  // Format date for display
  const formatDate = (date: Date) => {
    return format(date, 'EEE, MMM d, yyyy');
  };

  // Handle booking submission
  const handleBookAppointment = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!selectedDate || !selectedTimeSlot || !selectedEndTime) {
      setError('Please select a date and time for your appointment');
      return;
    }

    try {
      const result = await bookAppointmentMutation.mutateAsync({
        doctorId,
        appointmentDate: format(selectedDate, 'yyyy-MM-dd'),
        startTime: selectedTimeSlot,
        endTime: selectedEndTime,
        reason,
        appointmentType: appointmentType
      });

      if (result.success) {
        setSuccess(true);
        setError(null);
        logInfo('appointment', {
          action: 'book-appointment-success',
          doctorId,
          patientId: user?.uid,
          date: selectedDate.toISOString(),
          time: selectedTimeSlot
        });

        logValidation('4.11', 'success', 'Book appointment function fully operational with real data');

        // Redirect to appointments page after short delay
        setTimeout(() => {
          router.push('/patient/appointments');
        }, 1500);
      } else {
        setError(result.error || 'Failed to book appointment');
      }
    } catch (err) {
      setError('An error occurred while booking your appointment');
      console.error('Error booking appointment:', err);
    }
  };

  const isLoading = doctorLoading || availabilityLoading;
  const doctor = doctorData?.success ? doctorData.doctor : null;

  useEffect(() => {
    // Reset selected time slot when date changes
    setSelectedTimeSlot(null);
    setSelectedEndTime(null);
  }, [selectedDate]);

  if (doctorError || availabilityError) {
    return (
      <Alert variant="error">
        {doctorError instanceof Error ? doctorError.message : String(doctorError || availabilityError)}
      </Alert>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold">Book Appointment</h1>
        <Link href={`/doctor-profile/${doctorId}`}>
          <Button variant="outline" size="sm">
            <ChevronLeft className="h-4 w-4 mr-1" />
            Back to Profile
          </Button>
        </Link>
      </div>

      {error && <Alert variant="error">{error}</Alert>}

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Doctor Info Sidebar */}
        <div className="lg:col-span-1">
          <Card className="p-4 sticky top-4">
            {isLoading ? (
              <div className="flex justify-center py-8">
                <Spinner />
              </div>
            ) : doctor ? (
              <>
                <div className="flex items-center space-x-4 mb-4">
                  <div className="h-12 w-12 rounded-full bg-slate-200 dark:bg-slate-700 flex items-center justify-center text-slate-500">
                    {doctor.profilePictureUrl ? (
                      <Image
                        src={doctor.profilePictureUrl as string}
                        alt={`Dr. ${doctor.firstName} ${doctor.lastName}`}
                        className="h-12 w-12 rounded-full object-cover"
                        width={48}
                        height={48}
                      />
                    ) : (
                      <span className="text-lg font-bold">
                        {typeof doctor.firstName === 'string' ? doctor.firstName[0] : ''}
                        {typeof doctor.lastName === 'string' ? doctor.lastName[0] : ''}
                      </span>
                    )}
                  </div>
                  <div>
                    <h3 className="font-semibold">Dr. {doctor.firstName ?? ''} {doctor.lastName ?? ''}</h3>
                    <p className="text-sm text-slate-500 dark:text-slate-400">{doctor.specialty ?? ''}</p>
                  </div>
                </div>

                <div className="space-y-2 mb-4">
                  {doctor.location && (
                    <div className="flex items-start text-sm">
                      <MapPin className="h-4 w-4 mt-0.5 mr-2 text-slate-400" />
                      <span className="text-slate-600 dark:text-slate-300">{doctor.location ?? ''}</span>
                    </div>
                  )}
                  {doctor.consultationFee !== null && doctor.consultationFee !== undefined && (
                    <div className="flex items-center text-sm">
                      <Clock className="h-4 w-4 mr-2 text-slate-400" />
                      <span className="text-slate-600 dark:text-slate-300">
                        Consultation Fee: ${doctor.consultationFee}
                      </span>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <div className="text-center py-4 text-slate-500">
                Doctor information not available
              </div>
            )}

            {/* Selected appointment summary */}
            {selectedDate && selectedTimeSlot && (
              <div className="mt-4 pt-4 border-t border-slate-200 dark:border-slate-700">
                <h4 className="font-medium text-sm mb-2">Appointment Summary</h4>
                <div className="space-y-2">
                  <div className="flex items-center text-sm">
                    <CalendarDays className="h-4 w-4 mr-2 text-primary" />
                    <span>{formatDate(selectedDate)}</span>
                  </div>
                  <div className="flex items-center text-sm">
                    <Clock className="h-4 w-4 mr-2 text-primary" />
                    <span>{selectedTimeSlot} - {selectedEndTime}</span>
                  </div>
                  <div className="flex items-center text-sm">
                    {appointmentType === AppointmentType.IN_PERSON ? (
                      <>
                        <MapPin className="h-4 w-4 mr-2 text-primary" />
                        <span>In-person visit</span>
                      </>
                    ) : (
                      <>
                        <VideoIcon className="h-4 w-4 mr-2 text-primary" />
                        <span>Video consultation</span>
                      </>
                    )}
                  </div>
                </div>
              </div>
            )}
          </Card>
        </div>

        {/* Main Booking Form */}
        <div className="lg:col-span-2 space-y-6">
          {success ? (
            <Card className="p-6 text-center">
              <div className="flex justify-center mb-4">
                <div className="rounded-full bg-success/10 p-3">
                  <CheckCircle className="h-8 w-8 text-success" />
                </div>
              </div>
              <h2 className="text-xl font-semibold mb-2">Appointment Booked Successfully!</h2>
              <p className="text-slate-600 dark:text-slate-300 mb-6">
                Your appointment with Dr. {doctor?.firstName ?? ''} {doctor?.lastName ?? ''} has been confirmed.
              </p>
              <p className="text-sm text-slate-500 mb-4">
                Redirecting to your appointments...
              </p>
            </Card>
          ) : isLoading ? (
            <div className="flex justify-center py-12">
              <Spinner />
            </div>
          ) : (
            <form onSubmit={handleBookAppointment}>
              {/* Date Selection */}
              <Card className="p-6 mb-6">
                <h2 className="text-lg font-semibold mb-4">1. Select a Date</h2>
                <div className="grid grid-cols-2 md:grid-cols-5 gap-2">
                  {availableDates.map((date, index) => (
                    <button
                      key={index}
                      type="button"
                      className={`p-3 rounded-md border text-center transition-colors duration-200 ${
                        selectedDate && date.toDateString() === selectedDate.toDateString()
                          ? 'bg-primary/10 border-primary text-primary'
                          : 'border-slate-200 hover:border-primary/50 dark:border-slate-700'
                      }`}
                      onClick={() => setSelectedDate(date)}
                    >
                      <div className="font-medium">{format(date, 'EEE')}</div>
                      <div className="text-xl font-bold">{format(date, 'd')}</div>
                      <div className="text-xs">{format(date, 'MMM')}</div>
                    </button>
                  ))}
                </div>
              </Card>

              {/* Time Slot Selection */}
              {selectedDate && (
                <Card className="p-6 mb-6">
                  <h2 className="text-lg font-semibold mb-4">2. Select a Time Slot</h2>
                  {availableTimeSlots.length > 0 ? (
                    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-2">
                      {availableTimeSlots.map((slot, index: number) => (
                        <button
                          key={index}
                          type="button"
                          className={`py-2 px-3 rounded-md border text-center transition-colors duration-200 ${
                            slot.startTime === selectedTimeSlot
                              ? 'bg-primary/10 border-primary text-primary'
                              : 'border-slate-200 hover:border-primary/50 dark:border-slate-700'
                          }`}
                          onClick={() => {
                            setSelectedTimeSlot(slot.startTime);
                            setSelectedEndTime(slot.endTime);
                          }}
                        >
                          {slot.startTime}
                        </button>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8 text-slate-500">
                      <Calendar className="h-12 w-12 mx-auto mb-2 opacity-30" />
                      <p>No available time slots for this date</p>
                      <p className="text-sm mt-1">Please select another date</p>
                    </div>
                  )}
                </Card>
              )}

              {/* Appointment Type & Reason */}
              {selectedDate && selectedTimeSlot && (
                <Card className="p-6 mb-6">
                  <h2 className="text-lg font-semibold mb-4">3. Appointment Details</h2>

                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium mb-2">Appointment Type</label>
                      <div className="flex space-x-4">
                        <label className="flex items-center">
                          <input
                            type="radio"
                            className="h-4 w-4 text-primary border-slate-300"
                            checked={appointmentType === AppointmentType.IN_PERSON}
                            onChange={() => setAppointmentType(AppointmentType.IN_PERSON)}
                          />
                          <span className="ml-2 text-sm">In-person Visit</span>
                        </label>
                        <label className="flex items-center">
                          <input
                            type="radio"
                            className="h-4 w-4 text-primary border-slate-300"
                            checked={appointmentType === AppointmentType.VIDEO}
                            onChange={() => setAppointmentType(AppointmentType.VIDEO)}
                          />
                          <span className="ml-2 text-sm">Video Consultation</span>
                        </label>
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm font-medium mb-2">
                        Reason for Visit (Optional)
                      </label>
                      <Textarea
                        id="reason"
                        rows={3}
                        placeholder="Describe your symptoms or reason for the appointment"
                        value={reason}
                        onChange={(e) => setReason(e.target.value)}
                      />
                    </div>
                  </div>
                </Card>
              )}

              {/* Submit Button */}
              <div className="flex justify-end">
                <Button
                  type="submit"
                  disabled={!selectedDate || !selectedTimeSlot || bookAppointmentMutation.isPending}
                  isLoading={bookAppointmentMutation.isPending}
                >
                  Book Appointment
                </Button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
}

