import { format } from 'date-fns';
import { logInfo, logValidation } from '@/lib/logger';
import type { Appointment } from '@/types/schemas';

export default function DoctorAppointmentsPage() {
  const [view, setView] = useState<'list' | 'calendar'>('list');
  const [dateFilter, setDateFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [selectedAppointment, setSelectedAppointment] = useState<null | Appointment>(null);
  const [showCompleteModal, setShowCompleteModal] = useState(false);

  // Get appointments data from API
  const { data: appointmentsData, isLoading, error } = useDoctorAppointments();
  const completeMutation = useCompleteAppointment();
  const cancelMutation = useDoctorCancelAppointment();

  // Filter appointments based on selected filters
  const appointments = appointmentsData?.success ? appointmentsData.appointments : [];

  const filteredAppointments = appointments.filter((appointment: Appointment) => {
    // Date filtering
    const appointmentDate = new Date(appointment.appointmentDate);
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);

    const nextWeek = new Date(today);
    nextWeek.setDate(nextWeek.getDate() + 7);

    const nextMonth = new Date(today);
    nextMonth.setMonth(nextMonth.getMonth() + 1);

    let passesDateFilter = true;

    if (dateFilter === 'today') {
      passesDateFilter = appointmentDate.toDateString() === today.toDateString();
    } else if (dateFilter === 'tomorrow') {
      passesDateFilter = appointmentDate.toDateString() === tomorrow.toDateString();
    } else if (dateFilter === 'week') {
      passesDateFilter = appointmentDate >= today && appointmentDate <= nextWeek;
    } else if (dateFilter === 'month') {
      passesDateFilter = appointmentDate >= today && appointmentDate <= nextMonth;
    }

    // Status filtering
    let passesStatusFilter = true;

    if (statusFilter === 'scheduled') {
      passesStatusFilter = [AppointmentStatus.PENDING, AppointmentStatus.CONFIRMED].includes(appointment.status);
    } else if (statusFilter === 'completed') {
      passesStatusFilter = appointment.status === AppointmentStatus.COMPLETED;
    } else if (statusFilter === 'cancelled') {
      passesStatusFilter = appointment.status === AppointmentStatus.CANCELED;
    }

    return passesDateFilter && passesStatusFilter;
  });

  // Handle appointment completion
  const handleCompleteAppointment = async (id: string, notes: string) => {
    try {
      const result = await completeMutation.mutateAsync({
        appointmentId: id,
        notes
      });

      if (!result.success) {
        throw new Error(result.error);
      }

      setShowCompleteModal(false);
      logInfo('Appointment completed', { id });
      logValidation('4.10', 'success', 'Doctor appointment completion fully functional');

      return;
    } catch (error) {
      console.error('Error completing appointment:', error);
      throw error;
    }
  };

  // Handle appointment cancellation
  const handleCancelAppointment = async (id: string) => {
    if (confirm('Are you sure you want to cancel this appointment?')) {
      try {
        const result = await cancelMutation.mutateAsync({
          appointmentId: id,
          reason: 'Cancelled by doctor'
        });

        if (!result.success) {
          alert('Failed to cancel appointment: ' + result.error);
        }

        logInfo('Appointment cancelled', { id });
      } catch (err) {
        console.error('Error cancelling appointment:', err);
        alert('An error occurred while cancelling the appointment');
      }
    }
  };

  // Open complete modal with the selected appointment
  const openCompleteModal = (appointment: Appointment) => {
    setSelectedAppointment(appointment);
    setShowCompleteModal(true);
  };

  useEffect(() => {
    logInfo('Doctor appointments page loaded');
  }, []);

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
        <h1 className="text-2xl font-bold">Appointments</h1>

        {/* View Toggle */}
        <div className="flex items-center space-x-2 bg-slate-100 dark:bg-slate-800 rounded-lg p-1">
          <button
            className={clsx(
              'px-3 py-1.5 rounded-md flex items-center text-sm font-medium',
              view === 'list'
                ? 'bg-white dark:bg-slate-700 shadow-sm'
                : 'text-slate-500 dark:text-slate-400'
            )}
            onClick={() => setView('list')}
          >
            <ClipboardList className="h-4 w-4 mr-2" />
            List
          </button>
          <button
            className={clsx(
              'px-3 py-1.5 rounded-md flex items-center text-sm font-medium',
              view === 'calendar'
                ? 'bg-white dark:bg-slate-700 shadow-sm'
                : 'text-slate-500 dark:text-slate-400'
            )}
            onClick={() => setView('calendar')}
          >
            <Calendar className="h-4 w-4 mr-2" />
            Calendar
          </button>
        </div>
      </div>

      {/* Filters */}
      <Card className="p-4">
        <div className="flex flex-col sm:flex-row items-center gap-4">
          <div className="w-full sm:w-auto">
            <Select
              id="date-filter"
              label="Date"
              value={dateFilter}
              onChange={e => setDateFilter(e.target.value)}
              className="w-full sm:w-40"
            >
              <option value="all">All Dates</option>
              <option value="today">Today</option>
              <option value="tomorrow">Tomorrow</option>
              <option value="week">This Week</option>
              <option value="month">This Month</option>
            </Select>
          </div>
          <div className="w-full sm:w-auto">
            <Select
              id="status-filter"
              label="Status"
              value={statusFilter}
              onChange={e => setStatusFilter(e.target.value)}
              className="w-full sm:w-40"
            >
              <option value="all">All Statuses</option>
              <option value="scheduled">Scheduled</option>
              <option value="completed">Completed</option>
              <option value="cancelled">Cancelled</option>
            </Select>
          </div>
          <div className="flex-grow" />
        </div>
      </Card>

      {/* Loading, Error and Empty States */}
      {isLoading && (
        <div className="flex justify-center py-12">
          <Spinner />
        </div>
      )}

      {error && (
        <Alert variant="error" className="my-4">
          Error loading appointments: {error instanceof Error ? error.message : String(error)}
        </Alert>
      )}

      {!isLoading && !error && filteredAppointments.length === 0 && (
        <Card className="p-8 text-center">
          <Calendar className="h-12 w-12 mx-auto mb-4 text-slate-400" />
          <p className="text-slate-500 dark:text-slate-400">No appointments found matching your filters.</p>
          <Button 
            variant="outline" 
            size="sm" 
            className="mt-4"
            onClick={() => {
              setDateFilter('all');
              setStatusFilter('all');
            }}
          >
            Clear Filters
          </Button>
        </Card>
      )}

      {/* List View */}
      {view === 'list' && !isLoading && !error && filteredAppointments.length > 0 && (
        <div className="space-y-4">
          {filteredAppointments.map((appointment: Appointment) => (
            <AppointmentCard
              key={appointment.id}
              appointment={appointment}
              onCompleteClick={() => openCompleteModal(appointment)}
              onCancelClick={() => handleCancelAppointment(appointment.id)}
            />
          ))}
        </div>
      )}

      {/* Calendar View */}
      {view === 'calendar' && (
        <Card className="p-4">
          <div className="text-center p-8 text-slate-400 dark:text-slate-500">
            <Calendar className="h-16 w-16 mx-auto mb-4 opacity-30" />
            <p>Calendar view is not implemented in this static prototype.</p>
            <p className="text-sm mt-2">
              Would display a monthly calendar with daily appointment slots.
            </p>
          </div>
        </Card>
      )}

      {/* Complete Appointment Modal */}
      <CompleteAppointmentModal
        isOpen={showCompleteModal}
        onClose={() => setShowCompleteModal(false)}
        appt={selectedAppointment}
        onConfirm={handleCompleteAppointment}
      />
    </div>
  );
}

// Appointment Card Component
function AppointmentCard({
  appointment,
  onCompleteClick,
  onCancelClick,
}: {
  appointment: Appointment;
  onCompleteClick: () => void;
  onCancelClick: () => void;
}) {
  // Format date nicely
  const formattedDate = format(new Date(appointment.appointmentDate), 'PPPP');
  const isCompletable = appointment.status === AppointmentStatus.CONFIRMED;
  const isCancellable = [AppointmentStatus.PENDING, AppointmentStatus.CONFIRMED].includes(appointment.status);

  const statusMap: Record<string, string> = {
    [AppointmentStatus.PENDING]: 'Pending',
    [AppointmentStatus.CONFIRMED]: 'Confirmed',
    [AppointmentStatus.COMPLETED]: 'Completed',
    [AppointmentStatus.CANCELED]: 'Cancelled',
    [AppointmentStatus.RESCHEDULED]: 'Rescheduled'
  };

  const statusColor: Record<string, "success" | "default" | "warning" | "info" | "danger" | "pending"> = {
    [AppointmentStatus.PENDING]: 'pending',
    [AppointmentStatus.CONFIRMED]: 'info',
    [AppointmentStatus.COMPLETED]: 'success',
    [AppointmentStatus.CANCELED]: 'danger',
    [AppointmentStatus.RESCHEDULED]: 'warning',
  };

  return (
    <Card className="p-4">
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        {/* Patient Info & Time */}
        <div className="flex flex-col md:flex-row md:items-center gap-4">
          <div className="flex items-center">
            <div className="h-10 w-10 rounded-full bg-slate-200 dark:bg-slate-700 flex items-center justify-center text-slate-500 dark:text-slate-400 mr-3">
              <User className="h-5 w-5" />
            </div>
            <div>
              <h3 className="font-medium">{appointment.patientName}</h3>
              <Link
                href={`/patient-profile/${appointment.patientId}`}
                className="text-sm text-primary-600 dark:text-primary-400 hover:underline"
              >
                View Profile
              </Link>
            </div>
          </div>

          <div className="flex items-center md:ml-6">
            <div className="flex items-center text-sm text-slate-600 dark:text-slate-300 mr-4">
              <Calendar className="h-4 w-4 mr-2" />
              {formattedDate}
            </div>
            <div className="flex items-center text-sm text-slate-600 dark:text-slate-300">
              <Clock className="h-4 w-4 mr-2" />
              {appointment.startTime} - {appointment.endTime}
            </div>
          </div>
        </div>

        {/* Status Badge and Actions */}
        <div className="flex flex-wrap items-center justify-end gap-2">
          <Badge variant={statusColor[appointment.status] || 'default'}>
            {statusMap[appointment.status] || 'Unknown'}
          </Badge>

          <div className="flex items-center space-x-2">
            <Button size="sm" variant="outline" as={Link} href={`/doctor/appointments/${appointment.id}`}>
              <ChevronRight className="h-4 w-4 mr-1" />
              Details
            </Button>
            {isCompletable && (
              <Button size="sm" variant="primary" onClick={onCompleteClick}>
                <CheckCircle className="h-4 w-4 mr-1" />
                Complete
              </Button>
            )}
            {isCancellable && (
              <Button size="sm" variant="danger" onClick={onCancelClick}>
                <XCircle className="h-4 w-4 mr-1" />
                Cancel
              </Button>
            )}
          </div>
        </div>
      </div>

      {appointment.reason && (
        <p className="text-sm mt-2 text-slate-600 dark:text-slate-400">
          <strong>Reason:</strong> {appointment.reason}
        </p>
      )}

      {appointment.notes && (
        <p className="text-sm mt-1 text-slate-600 dark:text-slate-400">
          <strong>Notes:</strong> {appointment.notes}
        </p>
      )}
    </Card>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import Card from '@/components/ui/Card';
import Button from '@/components/ui/Button';
import Badge from '@/components/ui/Badge';
import Input from '@/components/ui/Input';
import Spinner from '@/components/ui/Spinner';
import Alert from '@/components/ui/Alert';
import { Calendar, Clock, Copy, Plus, Save, Trash2, Info } from 'lucide-react';
import { useDoctorAvailability, useSetAvailability } from '@/data/doctorLoaders';
import { logInfo, logValidation } from '@/lib/logger';
import { format } from 'date-fns';

// Days of the week
const weekdays = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];

// Time slots (simplified for demo)
const timeSlots = [
  '08:00', '09:00', '10:00', '11:00', '12:00', 
  '13:00', '14:00', '15:00', '16:00', '17:00'
];

type Weekday = 'monday'|'tuesday'|'wednesday'|'thursday'|'friday'|'saturday'|'sunday';
type WeeklySchedule = Record<Weekday, Array<{ startTime: string; endTime: string; isAvailable: boolean }>>;

const getNextTimeSlot = (time: string): string => {
  const [hours] = time.split(':');
  const nextHour = (parseInt(hours, 10) + 1) % 24;
  return `${nextHour.toString().padStart(2, '0')}:00`;
};

const createDefaultSchedule = (): WeeklySchedule => {
  const schedule = {} as WeeklySchedule;
  weekdays.forEach(day => {
    schedule[day as Weekday] = timeSlots.map(time => ({
      startTime: time,
      endTime: getNextTimeSlot(time),
      isAvailable: false
    }));
  });
  return schedule;
};

export default function DoctorAvailabilityPage() {
  // State for the weekly schedule
  const [weeklySchedule, setWeeklySchedule] = useState<WeeklySchedule>(createDefaultSchedule());

  // State for blocked dates
  const [blockedDates, setBlockedDates] = useState<string[]>([]);
  const [newBlockDate, setNewBlockDate] = useState('');
  const [newBlockReason, setNewBlockReason] = useState('');

  // Load doctor availability
  const { data, isLoading, error, refetch } = useDoctorAvailability();
  const setAvailabilityMutation = useSetAvailability();

  // Initialize state from API data
  useEffect(() => {
    if (data?.success) {
      const { availability } = data;

      // Initialize weekly schedule
      if (availability.weeklySchedule) {
        setWeeklySchedule({ ...createDefaultSchedule(), ...availability.weeklySchedule });
      } else {
        setWeeklySchedule(createDefaultSchedule());
      }

      // Initialize blocked dates
      if (availability.blockedDates) {
        setBlockedDates(Array.isArray(availability.blockedDates) ? availability.blockedDates : []);
      }
    }
  }, [data]);

  // Toggle availability for a time slot
  const toggleAvailability = (day: string, timeIndex: number) => {
    setWeeklySchedule(prev => {
      const newSchedule = { ...prev };
      if (!newSchedule[day as Weekday]) {
        newSchedule[day as Weekday] = [];
      }
      const updatedSlots = [...(newSchedule[day as Weekday] || [])];
      updatedSlots[timeIndex] = {
        ...updatedSlots[timeIndex],
        isAvailable: !updatedSlots[timeIndex]?.isAvailable
      };
      newSchedule[day as Weekday] = updatedSlots;
      return newSchedule;
    });
  };

  // Handle saving availability
  const handleSave = async () => {
    try {
      const result = await setAvailabilityMutation.mutateAsync({
        weeklySchedule,
        blockedDates
      });

      if (result.success) {
        logInfo('Doctor availability saved successfully');
        logValidation('4.11', 'success', 'Doctor availability setting is fully functional with real data');
        await refetch();
      } else {
        console.error('Error saving availability:', result.error);
      }
    } catch (err) {
      console.error('Error saving availability:', err);
    }
  };

  // Handle adding a blocked date
  const handleAddBlockedDate = () => {
    if (!newBlockDate) return;

    if (!blockedDates.includes(newBlockDate)) {
      setBlockedDates([...blockedDates, newBlockDate]);
      setNewBlockDate('');
      setNewBlockReason('');
    }
  };

  // Handle removing a blocked date
  const handleRemoveBlockedDate = (dateToRemove: string) => {
    setBlockedDates(blockedDates.filter(date => date !== dateToRemove));
  };

  // Format day name for display
  const formatDayName = (day: string) => {
    return day.charAt(0).toUpperCase() + day.slice(1);
  };

  // Check if a slot is available
  const isAvailable = (day: string, timeIndex: number) => {
    return weeklySchedule[day as Weekday]?.[timeIndex]?.isAvailable || false;
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold">Manage Availability</h1>
        <Button 
          variant="primary" 
          onClick={handleSave}
          isLoading={setAvailabilityMutation.isPending}
          disabled={setAvailabilityMutation.isPending}
        >
          <Save className="h-4 w-4 mr-2" />
          Save Settings
        </Button>
      </div>

      {error && (
        <Alert variant="error">Error loading availability: {error.toString()}</Alert>
      )}

      {setAvailabilityMutation.isError && (
        <Alert variant="error">Error saving availability changes</Alert>
      )}

      {/* Loading State */}
      {isLoading && (
        <div className="flex justify-center py-12">
          <Spinner />
        </div>
      )}

      {!isLoading && (
        <>
          {/* Weekly Schedule */}
          <Card>
            <div className="p-4 border-b border-slate-200 dark:border-slate-700 flex justify-between items-center">
              <div className="flex items-center">
                <Clock className="h-5 w-5 mr-2 text-primary" />
                <h2 className="text-lg font-medium">Weekly Schedule</h2>
              </div>
            </div>
            <div className="p-4 overflow-x-auto">
              <div className="inline-block min-w-full align-middle">
                <table className="min-w-full divide-y divide-slate-200 dark:divide-slate-700">
                  <thead>
                    <tr>
                      <th className="py-3 px-4 text-left text-xs font-medium text-slate-500 dark:text-slate-400 uppercase tracking-wider">
                        Time / Day
                      </th>
                      {weekdays.map(day => (
                        <th
                          key={day}
                          className="py-3 px-4 text-center text-xs font-medium text-slate-500 dark:text-slate-400 uppercase tracking-wider"
                        >
                          {formatDayName(day)}
                        </th>
                      ))}
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-slate-200 dark:divide-slate-700">
                    {timeSlots.map((time, timeIndex) => (
                      <tr key={time} className="hover:bg-slate-50 dark:hover:bg-slate-800/50">
                        <td className="py-2 px-4 text-sm text-slate-600 dark:text-slate-300 font-medium">
                          {time}
                        </td>
                        {weekdays.map(day => (
                          <td key={`${day}-${time}`} className="py-2 px-2 text-center">
                            <div className="flex justify-center">
                              <input
                                type="checkbox"
                                checked={isAvailable(day, timeIndex)}
                                onChange={() => toggleAvailability(day, timeIndex)}
                                className="h-5 w-5 text-primary-600 focus:ring-primary-500 border-slate-300 dark:border-slate-600 rounded"
                              />
                            </div>
                          </td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <div className="mt-4 p-3 rounded-md bg-slate-50 dark:bg-slate-800 text-sm text-slate-600 dark:text-slate-300">
                <Info className="h-4 w-4 inline mr-1 text-info" />
                Check the boxes for times when you&apos;re regularly available. This schedule will
                repeat every week.
              </div>
            </div>
          </Card>

          {/* Block Specific Dates */}
          <Card>
            <div className="p-4 border-b border-slate-200 dark:border-slate-700">
              <div className="flex items-center">
                <Calendar className="h-5 w-5 mr-2 text-primary" />
                <h2 className="text-lg font-medium">Block Specific Dates</h2>
              </div>
            </div>
            <div className="p-4">
              <div className="flex flex-col md:flex-row gap-4 mb-4">
                <div className="flex-grow md:max-w-xs">
                  <Input
                    id="block-date"
                    type="date"
                    label="Date to Block"
                    value={newBlockDate}
                    onChange={e => setNewBlockDate(e.target.value)}
                  />
                </div>
                <div className="flex-grow">
                  <Input
                    id="block-reason"
                    label="Reason (Optional)"
                    placeholder="Vacation, Conference, etc."
                    value={newBlockReason}
                    onChange={e => setNewBlockReason(e.target.value)}
                  />
                </div>
                <div className="flex-shrink-0 flex items-end">
                  <Button onClick={handleAddBlockedDate} disabled={!newBlockDate}>
                    <Plus className="h-4 w-4 mr-2" />
                    Add Date
                  </Button>
                </div>
              </div>

              {/* Blocked Dates List */}
              {blockedDates.length > 0 ? (
                <div className="space-y-2 mt-6">
                  <h3 className="text-sm font-medium text-slate-600 dark:text-slate-300 mb-2">
                    Currently Blocked Dates:
                  </h3>
                  {blockedDates.map(date => (
                    <div
                      key={date}
                      className="flex items-center justify-between p-3 bg-slate-50 dark:bg-slate-800 rounded-md"
                    >
                      <div className="flex items-center space-x-3">
                        <Badge variant="warning">
                          {format(new Date(date), 'EEE, MMMM d, yyyy')}
                        </Badge>
                      </div>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleRemoveBlockedDate(date)}
                      >
                        <Trash2 className="h-4 w-4 text-danger" />
                      </Button>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center p-6 text-slate-400 dark:text-slate-500">
                  <Calendar className="h-12 w-12 mx-auto mb-2 opacity-30" />
                  <p>No blocked dates set</p>
                </div>
              )}
            </div>
          </Card>
        </>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useAuth } from '@/context/AuthContext';
import { useNotifications, useDirectMessage } from '@/data/sharedLoaders';
import Card from '@/components/ui/Card';
import Button from '@/components/ui/Button';
import Input from '@/components/ui/Input';
import Textarea from '@/components/ui/Textarea';
import Alert from '@/components/ui/Alert';
import Spinner from '@/components/ui/Spinner';
import { MessageSquare, Send, User } from 'lucide-react';
import { logInfo } from '@/lib/logger';
import { NotificationType } from '@/types/enums';
import { formatDistanceToNow } from 'date-fns';
import type { Notification } from '@/types/schemas';

export default function MessagesPage() {
  const { user } = useAuth();
  const [recipientId, setRecipientId] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);

  // Get notifications with message type
  const { data: notificationsData, isLoading: notificationsLoading } = useNotifications();

  // Direct message mutation
  const directMessageMutation = useDirectMessage();

  // Filter for message-type notifications
  const messageNotifications = notificationsData?.success 
    ? notificationsData.notifications.filter((n: Notification) => n.type === NotificationType.NEW_MESSAGE)
    : [];

  // Form submission handler
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setSuccessMessage(null);

    if (!recipientId) {
      setError('Please enter a recipient ID');
      return;
    }

    if (!message) {
      setError('Please enter a message');
      return;
    }

    try {
      const result = await directMessageMutation.mutateAsync({
        recipientId,
        subject: subject || 'New Message',
        message
      });

      if (result.success) {
        logInfo('messages', { action: 'send-message', recipientId, userId: user?.uid });
        setSuccessMessage('Message sent successfully');
        setMessage('');
        setSubject('');
      } else {
        setError(result.error || 'Failed to send message');
      }
    } catch (err) {
      setError('An error occurred while sending the message');
      console.error(err);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6 flex items-center gap-2">
        <MessageSquare /> Messages
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <Card className="p-4">
          <h2 className="text-xl font-semibold mb-4">Send a Message</h2>

          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label htmlFor="recipientId" className="block text-sm font-medium mb-1">
                Recipient ID
              </label>
              <Input
