'use client';

import React, { useState } from 'react';
import Card from '@/components/ui/Card';
import Button from '@/components/ui/Button';
import Select from '@/components/ui/Select';
import Textarea from '@/components/ui/Textarea';
import Alert from '@/components/ui/Alert';
import { CheckCircle, XCircle, AlertCircle } from 'lucide-react';

interface VerificationFormProps {
  doctorId: string;
  currentStatus: 'PENDING' | 'VERIFIED' | 'REJECTED';
  onSubmit: (payload: { status: string; notes: string }) => Promise<void>;
}

export default function VerificationForm({ 
  doctorId, 
  currentStatus, 
  onSubmit 
}: VerificationFormProps) {
  const [status, setStatus] = useState(currentStatus);
  const [notes, setNotes] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  // Check if form can be submitted
  const canSubmit = () => {
    if (status === currentStatus) return false;
    if (status === 'REJECTED' && !notes.trim()) return false;
    return true;
  };

  // Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!canSubmit()) return;

    setLoading(true);
    setError('');
    setSuccess(false);

    try {
      // Simulate network delay as specified
      await new Promise(r => setTimeout(r, 700));
      await onSubmit({ status, notes });
      setSuccess(true);
    } catch (err) {
      setError('Failed to update verification status. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card className="mt-6">
      <div className="p-4 border-b border-slate-200 dark:border-slate-700">
        <h2 className="text-lg font-semibold">Verification Decision</h2>
      </div>

      <form onSubmit={handleSubmit} className="p-4 space-y-4">
        {error && (
          <Alert variant="error" className="mb-4">
            <AlertCircle className="h-4 w-4 mr-2" />
            {error}
          </Alert>
        )}

        {success && (
          <Alert variant="success" className="mb-4">
            <CheckCircle className="h-4 w-4 mr-2" />
            Doctor verification status updated successfully.
          </Alert>
        )}

        <div>
          <label htmlFor="status" className="block text-sm font-medium mb-1">
            Verification Status
          </label>
          <Select
            id="status"
            value={status}
            onChange={(e) => setStatus(e.target.value as 'PENDING' | 'VERIFIED' | 'REJECTED')}
            className="w-full"
          >
            <option value="PENDING">Pending Verification</option>
            <option value="VERIFIED">Verified</option>
            <option value="REJECTED">Rejected</option>
          </Select>
        </div>

        <div>
          <label htmlFor="notes" className="block text-sm font-medium mb-1">
            Notes {status === 'REJECTED' && <span className="text-danger">*</span>}
          </label>
          <Textarea
            id="notes"
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            placeholder={
              status === 'REJECTED'
                ? 'Please provide a reason for rejection...'
                : 'Optional notes about the verification decision...'
            }
            required={status === 'REJECTED'}
            rows={4}
          />
          {status === 'REJECTED' && !notes.trim() && (
            <p className="mt-1 text-sm text-danger">
              Notes are required when rejecting a verification request.
            </p>
          )}
        </div>

        <div className="flex items-center space-x-3 pt-2">
          <Button
            type="submit"
            disabled={!canSubmit() || loading}
            isLoading={loading}
            variant={status === 'VERIFIED' ? 'primary' : status === 'REJECTED' ? 'danger' : 'secondary'}
          >
            {status === 'VERIFIED' && <CheckCircle className="h-4 w-4 mr-2" />}
            {status === 'REJECTED' && <XCircle className="h-4 w-4 mr-2" />}
            Confirm Decision
          </Button>
        </div>
      </form>
    </Card>
  );
}

'use client';

import { useEffect, useState, useRef } from 'react';
import { useRouter, usePathname } from 'next/navigation';
import { Loader2 } from 'lucide-react';
import { useAuth } from '@/context/AuthContext';
import { logInfo } from '@/lib/logger';
import { UserType } from '@/types/enums';

interface ProtectedProps {
  children: React.ReactNode;
  roles?: UserType[]; // Optional array of allowed roles
}

/**
 * Protected route component that enforces authentication and optional role-based access
 * Shows a spinner while loading, redirects to login if not authenticated,
 * and redirects to unauthorized page if user doesn't have required role
 */
export default function Protected({ children, roles }: ProtectedProps) {
  const { user, profile, isLoading } = useAuth();
  const router = useRouter();
  const pathname = usePathname();
  const [isAuthorized, setIsAuthorized] = useState(false);
  const redirectAttempted = useRef(false);

  useEffect(() => {
    // Only check auth after isLoading is false
    if (isLoading) {
      return;
    }

    // Prevent multiple redirects in a short time span
    if (redirectAttempted.current) {
      return;
    }

    // If no user is logged in (after loading), redirect to login
    if (!user) {
      logInfo('Auth guard: Redirecting to login', { pathname });
      redirectAttempted.current = true;
      setTimeout(() => {
        const redirectPath = pathname ? `/auth/login?next=${encodeURIComponent(pathname)}` : '/auth/login';
        router.push(redirectPath);
        setTimeout(() => {
          redirectAttempted.current = false;
        }, 5000);
      }, 100);
      return;
    }

    // If roles are specified, check if user has required role
    if (roles && roles.length > 0) {
      const userType = profile?.userType;
      const hasRequiredRole = userType && roles.includes(userType as UserType);
      if (!hasRequiredRole) {
        logInfo('Auth guard: Unauthorized access', {
          userType: userType || 'unknown',
          requiredRoles: roles,
        });
        redirectAttempted.current = true;
        setTimeout(() => {
          router.push('/unauthorized');
          setTimeout(() => {
            redirectAttempted.current = false;
          }, 5000);
        }, 100);
        return;
      }
    }

    // User is authenticated and authorized
    setIsAuthorized(true);
  }, [user, profile, isLoading, roles, router, pathname]);

  // Show loading spinner while authentication state is loading
  // or while authorization is being checked
  if (isLoading || !isAuthorized) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Loader2 className="animate-spin text-primary h-8 w-8" />
      </div>
    );
  }

  // User is authenticated and authorized, render children
  return <>{children}</>;
}

'use client';

import React, { useState, useEffect, Fragment } from 'react';
import { Dialog, Transition } from '@headlessui/react';
import { CheckCircle, AlertTriangle } from 'lucide-react';
import Button from '@/components/ui/Button';
import Textarea from '@/components/ui/Textarea';
import Alert from '@/components/ui/Alert';
import type { Appointment } from '@/types/schemas';

interface CompleteModalProps {
  isOpen: boolean;
  onClose: () => void;
  appt: Appointment | null;
  onConfirm: (appointmentId: string, notes: string) => Promise<void>;
}

/**
 * Modal component for completing appointments
 * Uses Headless UI Dialog for accessibility and keyboard interactions
 */
export default function CompleteAppointmentModal({
  isOpen,
  onClose,
  appt,
  onConfirm,
}: CompleteModalProps) {
  const [notes, setNotes] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Reset state when modal opens/closes
  useEffect(() => {
    if (!isOpen) {
      // Small delay to avoid visual flickering during close animation
      const timer = setTimeout(() => {
        setNotes('');
        setError('');
        setLoading(false);
      }, 200);
      return () => clearTimeout(timer);
    }
  }, [isOpen]);

  // Handle confirmation with loading state and error handling
  const handleConfirm = async () => {
    if (!appt) return;

    setLoading(true);
    setError('');

    try {
      // Simulate network delay as specified in requirements
      await new Promise(resolve => setTimeout(resolve, 800));
      await onConfirm(appt.id, notes.trim());
    } catch (err) {
      setError('Failed to complete appointment. Please try again.');
      setLoading(false);
    }
  };

  if (!appt) return null;

  return (
    <Transition.Root appear show={isOpen} as={Fragment}>
      <Dialog
        as="div"
        className="relative z-50"
        onClose={() => !loading && onClose()}
      >
        <Transition.Child
          as={Fragment}
