'use client';

import React, { useState, Fragment } from 'react';
import { Dialog, Transition } from '@headlessui/react';
import { XCircle, AlertTriangle } from 'lucide-react';
import Button from '@/components/ui/Button';
import Textarea from '@/components/ui/Textarea';
import Alert from '@/components/ui/Alert';

interface CancelModalProps {
  isOpen: boolean;
  onClose: () => void;
  appt: {
    id: string;
    doctorName: string;
    date: string; // ISO
    time: string;
  } | null;
  onConfirm: (id: string, reason: string) => Promise<void>;
}

/**
 * Modal component for cancelling appointments
 * Uses Headless UI Dialog for accessibility and keyboard interactions
 */
export default function CancelAppointmentModal({
  isOpen,
  onClose,
  appt,
  onConfirm,
}: CancelModalProps) {
  const [reason, setReason] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Reset state when modal opens/closes
  React.useEffect(() => {
    if (!isOpen) {
      // Small delay to avoid visual flickering during close animation
      const timer = setTimeout(() => {
        setReason('');
        setError('');
        setLoading(false);
      }, 200);
      return () => clearTimeout(timer);
    }
  }, [isOpen]);

  // Handle confirmation with loading state and error handling
  const handleConfirm = async () => {
    if (!appt) return;
    if (!reason.trim()) {
      setError('Please provide a reason for cancellation');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Simulate network delay
      await new Promise(resolve => setTimeout(resolve, 800));
      await onConfirm(appt.id, reason.trim());
    } catch (err) {
      setError('Failed to cancel appointment. Please try again.');
      setLoading(false);
    }
  };

  if (!appt) return null;

  return (
    <Transition.Root appear show={isOpen} as={Fragment}>
      <Dialog
        as="div"
        className="relative z-50"
        onClose={() => !loading && onClose()}
      >
        <Transition.Child
          as={Fragment}
          enter="ease-out duration-300"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="ease-in duration-200"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <div className="fixed inset-0 bg-black/30 backdrop-blur-sm" aria-hidden="true" />
        </Transition.Child>

        <div className="fixed inset-0 flex items-center justify-center p-4">
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0 scale-95"
            enterTo="opacity-100 scale-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100 scale-100"
            leaveTo="opacity-0 scale-95"
          >
            <Dialog.Panel className="w-full max-w-md rounded-lg bg-white dark:bg-slate-800 p-6 shadow-xl">
              <div className="flex items-center space-x-2 text-danger mb-4">
                <XCircle className="h-6 w-6" />
                <Dialog.Title className="text-lg font-medium">
                  Cancel Appointment
                </Dialog.Title>
              </div>

              <div className="mt-2">
                <p className="text-slate-600 dark:text-slate-300 mb-4">
                  Are you sure you want to cancel your appointment with <strong>{appt.doctorName}</strong> on{' '}
                  <strong>{new Date(appt.date).toLocaleDateString()}</strong> at{' '}
                  <strong>{appt.time}</strong>?
                </p>

                {error && (
                  <Alert variant="error" className="mb-4">
                    <div className="flex items-center space-x-2">
                      <AlertTriangle size={16} />
                      <span>{error}</span>
                    </div>
                  </Alert>
                )}

                <Textarea
                  id="cancellation-reason"
                  label="Reason for cancellation"
                  placeholder="Please provide a reason for cancelling this appointment..."
                  value={reason}
                  onChange={(e) => setReason(e.target.value)}
                  disabled={loading}
                  required
                  rows={3}
                  className="mb-4"
                />

                <div className="mt-6 flex justify-end space-x-3">
                  <Button
                    variant="secondary"
                    onClick={onClose}
                    disabled={loading}
                  >
                    Keep Appointment
                  </Button>
                  <Button
                    variant="danger"
                    onClick={handleConfirm}
                    isLoading={loading}
                  >
                    Confirm Cancellation
                  </Button>
                </div>
              </div>
            </Dialog.Panel>
          </Transition.Child>
        </div>
      </Dialog>
    </Transition.Root>
  );
}

import { logInfo } from '@/lib/logger';

/**
 * Application Configuration Module
 * Centralizes access to environment variables and derived settings
 * 
 * This module should be imported early in the application bootstrap process
 * to ensure consistent environment variable access throughout the app.
 */

/**
 * API Mode Setting
 * Controls whether the application uses real or mock API endpoints
 * 
 * Values:
 * - "live": Uses actual API endpoints for real data
 * - "mock": Uses mock data for development/testing
 */
export const API_MODE = process.env.NEXT_PUBLIC_API_MODE || 'mock';

/**
 * Logging Level Setting
 * Controls the verbosity of application logging
 * 
 * Values:
 * - "debug": Most verbose, shows all logs including detailed diagnostics
 * - "info": Shows operational information, general app flow
 * - "warn": Shows potential issues that don't prevent operation
 * - "error": Shows only errors that affect functionality
 */
export const LOG_LEVEL = process.env.NEXT_PUBLIC_LOG_LEVEL || 'info';

/**
 * Development Environment Flag
 * Determines if the application is running in development mode
 */
export const IS_DEVELOPMENT = process.env.NODE_ENV === 'development';

/**
 * Mock API Mode Flag
 * Derived from API_MODE, provides a boolean for conditional logic
 */
export const IS_MOCK_MODE = API_MODE === 'mock';

/**
 * Log the application configuration
 * Called during application initialization to record the environment setup
 */
const logAppConfig = () => {
  logInfo('Application Configuration Loaded', {
    API_MODE,
    LOG_LEVEL,
    IS_DEVELOPMENT,
    IS_MOCK_MODE,
    NODE_ENV: process.env.NODE_ENV
  });
};

// Execute logging on module import
logAppConfig();

export default {
  API_MODE,
  LOG_LEVEL,
  IS_DEVELOPMENT,
  IS_MOCK_MODE
};

'use client';

import React, { createContext, useContext, useState, useEffect, useCallback, ReactNode } from 'react';
import { useRouter } from 'next/navigation';
import { logInfo, logError, logValidation } from '@/lib/logger';
import { loadSession, saveSession } from '@/lib/localSession';
import { callApi } from '@/lib/apiClient';
import localApi from '@/lib/localApiFunctions';
import type { UserProfile, PatientProfile, DoctorProfile } from '@/types/schemas';
import { UserType } from '@/types/enums';
import { roleToDashboard } from '@/lib/router';
import { mockUserData } from '@/lib/mockData';
import { z } from 'zod';

// Add TypeScript augmentation for the window.__mockLogin helper
declare global {
  interface Window {
    __mockLogin: (role?: string) => Promise<boolean> | undefined;
  }
}

// Type definitions for registration payloads
export interface PatientRegistrationPayload {
  email: string;
  password: string;
  firstName: string;
  lastName: string;
  dateOfBirth: string;
  gender: string;
  bloodType?: string;
  medicalHistory?: string;
  userType: UserType;
}

export interface DoctorRegistrationPayload {
  email: string;
  password: string;
  firstName: string;
  lastName: string;
  specialty: string;
  licenseNumber: string;
  yearsOfExperience: number;
  userType: UserType;
}

// Type for the User object
interface User {
  uid: string;
  email?: string;
  role: UserType;
}

// Context value type
interface AuthContextType {
  user: User | null;
  profile: UserProfile & { id: string } | null;
  patientProfile: PatientProfile & { id: string } | null;
  doctorProfile: DoctorProfile & { id: string } | null;
  isLoading: boolean;
  error: string | null;
  login: (email: string, password: string, skipMock?: string) => Promise<boolean>;
  logout: () => void;
  refreshProfile: () => Promise<void>;
  clearError: () => void;
  registerPatient: (payload: PatientRegistrationPayload) => Promise<any>;
  registerDoctor: (payload: DoctorRegistrationPayload) => Promise<any>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);
const SESSION_KEY = 'healthAppSession';

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export function AuthProvider({ children }: { children: ReactNode }) {
  const router = useRouter();
  const [user, setUser] = useState<User | null>(null);
  const [profile, setProfile] = useState<UserProfile & { id: string } | null>(null);
  const [patientProfile, setPatientProfile] = useState<PatientProfile & { id: string } | null>(null);
  const [doctorProfile, setDoctorProfile] = useState<DoctorProfile & { id: string } | null>(null);
  const [isLoading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [initialized, setInitialized] = useState<boolean>(false);

  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Initialize auth state from localStorage
  useEffect(() => {
    const session = loadSession();
    console.log('[AuthContext] Loaded session from localStorage:', session);
    if (session) {
      // Ensure the session object conforms to the User type
      if (session.uid && session.role) {
        setUser({
          uid: session.uid,
          email: session.email,
          role: session.role as UserType
        });
        console.log('[AuthContext] Session valid, setting user:', session);
        // Do NOT call refreshProfile here!
      } else {
        console.warn('[AuthContext] Session missing uid or role:', session);
      }
    } else {
      console.log('[AuthContext] No session found in localStorage.');
    }
    setLoading(false);
    setInitialized(true);
  }, []);

  // Call refreshProfile only after user is set
  useEffect(() => {
    if (user) {
      refreshProfile();
    }
  }, [user]);

  const logout = () => {
    console.log('[AuthContext] Logging out, clearing session and state.');
    setUser(null);
    setProfile(null);
    setPatientProfile(null);
    setDoctorProfile(null);
    saveSession(null);
    router.push('/auth/login');
  };

  const refreshProfile = async () => {
    if (!user) {
      console.warn('[AuthContext] refreshProfile called with no user.');
      return;
    }
    try {
      setLoading(true);
      console.log('[AuthContext] Refreshing profile for user:', user);
      const response = await callApi('getMyUserProfile', { uid: user.uid, role: user.role });
      console.log('[AuthContext] getMyUserProfile response:', response);
      if (response) {
        setProfile(response);
      } else {
        logError('Failed to load user profile', { uid: user.uid });
      }
    } catch (err) {
      logError('Error refreshing profile', err);
    } finally {
      setLoading(false);
    }
  };

  const login = async (email: string, password: string, skipMock?: string) => {
    setLoading(true);
    console.log('[AuthContext] login called:', { email, password: !!password, skipMock });
    try {
      if (typeof email !== 'string' || !email) {
        throw new Error('Invalid email format');
      }
      const result = await callApi('login', { email, password });
      console.log('[AuthContext] login result:', result);
      if (result.success) {
        const { user: u, userProfile } = result;
        const mappedUser: User = {
          uid: u.id,
          email: u.email || undefined,
          role: userProfile.userType as UserType
        };
        setUser(mappedUser);
        saveSession(mappedUser);
        setProfile(userProfile);
        console.log('[AuthContext] Login success, user set and session saved:', mappedUser);
        logInfo('Auth login success', {
          uid: u.id,
          email: u.email,
          userType: userProfile.userType,
        });
        const dashboardPath = roleToDashboard(userProfile.userType as UserType);
        router.push(dashboardPath);
        return true;
      } else {
        setError(result.error || 'Invalid credentials');
        logError('Auth login failed', { error: result.error });
        return false;
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error during login';
      setError(errorMessage);
      logError('Auth login error:', err);
      return false;
    } finally {
      setLoading(false);
    }
  };

  // Add global helper for mock login, bypassed if real login is in progress
  if (typeof window !== 'undefined') {
    window.__mockLogin = (mockType?: string) => {
      console.log('Mock login called with:', mockType, typeof mockType);

      // Skip mock login if an actual login is in progress
      if (mockType === 'ACTUAL_LOGIN_IN_PROGRESS') {
        console.log('Skipping mock login because real login is in progress');
        return;
      }

      const typeMap: Record<string, { email: string; password: string }> = {
        PATIENT: { email: 'test-patient@example.com', password: 'password' },
        DOCTOR: { email: 'test-doctor@example.com', password: 'password' },
        ADMIN: { email: 'admin@example.com', password: 'password' },
        // Add lowercase options for better compatibility
        patient: { email: 'test-patient@example.com', password: 'password' },
        doctor: { email: 'test-doctor@example.com', password: 'password' },
        admin: { email: 'admin@example.com', password: 'password' },
      };

      // Handle the case when mockType is an email address
      if (mockType && typeof mockType === 'string' && mockType.includes('@')) {
        console.log('Using provided email as mock login credential:', mockType);
        return login(mockType, 'password');
      }

      const loginData = mockType && typeof mockType === 'string' && typeMap[mockType]
        ? typeMap[mockType]
        : typeMap.PATIENT;

      if (!loginData || !loginData.email) {
        console.error('Invalid mock login data');
        return Promise.resolve(false);
      }

      console.log('Using mock credentials:', loginData);
      return login(loginData.email, loginData.password);
    };
  }

  return (
    <AuthContext.Provider
      value={{
        user,
        profile,
        patientProfile,
        doctorProfile,
        isLoading,
        error,
        login,
        logout,
        refreshProfile,
        clearError,
        registerPatient: async (payload) => {
          try {
            const result = await callApi('registerPatient', payload);
            return result.success;
          } catch (err) {
            logError('Error registering patient', err);
            return false;
          }
        },
        registerDoctor: async (payload) => {
          try {
            const result = await callApi('registerDoctor', payload);
            return result.success;
          } catch (err) {
            logError('Error registering doctor', err);
            return false;
          }
        },
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { callApi } from '@/lib/apiClient';
import { useAuth } from '@/context/AuthContext';
import { UserType, VerificationStatus, AccountStatus } from '@/types/enums';

/**
 * Hook to fetch all users
 */
export const useAllUsers = () => {
  const { user } = useAuth();

  return useQuery({
    queryKey: ['admin', 'users'],
    queryFn: async () => {
      if (!user || user.role !== UserType.ADMIN) {
        throw new Error('Unauthorized');
      }

      return callApi('adminGetAllUsers', {
        uid: user.uid,
        role: UserType.ADMIN
      });
    },
    enabled: !!user && user.role === UserType.ADMIN
  });
};

/**
 * Hook to fetch all patients
 */
export const useAllPatients = () => {
  const { user } = useAuth();

  return useQuery({
    queryKey: ['admin', 'patients'],
    queryFn: async () => {
      if (!user || user.role !== UserType.ADMIN) {
        throw new Error('Unauthorized');
      }

      const response = await callApi('adminGetAllUsers', {
        uid: user.uid,
        role: UserType.ADMIN
      });

      // Filter for patients only
      return {
        ...response,
        users: response.users.filter((u: {role: string}) => u.role === UserType.PATIENT)
      };
    },
    enabled: !!user && user.role === UserType.ADMIN
  });
};

