        });
      });
    }

    logInfo('registerUser: account created', { uid, role: data.userType });
    return { success: true, userId: uid };
  } catch (e) {
    logError('registerUser failed', e);
    return { success: false, error: 'Internal error during registration' };
  } finally {
    perf.stop();
  }
}

